select bizno as 사업자등록번호, bizcst_nm as 세금계산서고객명 , sum("202407_M_수량") as "202407_M_수량" , sum("202407_M_금액") as "202407_M_금액" from ( SELECT LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.BIZNO     , LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.BIZCST_NM   	, CASE WHEN substr(LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.COC_DT,1,7) =  '2024-07' THEN       	SUM(CASE  		WHEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DAT_DCD) NOT IN ('08') 			THEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.VAT_EXCL_ADPT_AMT) 		ELSE 0 		END) + SUM(CASE  		WHEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DAT_DCD) NOT IN ('08') 			THEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.VAT_EXCL_ADPT_EXPT_AMT) 		ELSE 0 		END) + SUM(CASE  		WHEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DAT_DCD) NOT IN ('08') 			THEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.VAT_EXCL_CRDT_AMT) 		ELSE 0 		END) + SUM(CASE  		WHEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DAT_DCD) NOT IN ('08') 			THEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.VAT_EXCL_CODP_AMT) 		ELSE 0 		END)     ELSE 0 END AS "202407_M_금액"     , CASE WHEN substr(LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.COC_DT,1,7) =  '2024-07' THEN       	SUM(CASE  		WHEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DAT_DCD) NOT IN ( 				'04' 				,'05' 				,'07' 				,'08' 				) 			THEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.ADPT_QTY) 		ELSE 0 		END) + SUM(CASE  		WHEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DAT_DCD) NOT IN ( 				'04' 				,'05' 				,'07' 				,'08' 				) 			THEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.ADPT_PREARR_QTY) 		ELSE 0 		END) + SUM(CASE  		WHEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DAT_DCD) NOT IN ( 				'04' 				,'05' 				,'07' 				,'08' 				) 			THEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.CRDT_QTY) 		ELSE 0 		END) + SUM(CASE  		WHEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DAT_DCD) NOT IN ( 				'04' 				,'05' 				,'07' 				,'08' 				) 			THEN (LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.CODP_QTY) 		ELSE 0 		END)     ELSE 0 END AS "202407_M_수량" FROM LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P INNER JOIN LOGIS_ADMIN.IAMD_TB_MNGBRAN_M M ON LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.BRAN_CD = M.MNG_BRAN_CD INNER JOIN LOGIS_ADMIN.IAML_TB_BIDEALDIV_D ON LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DEAL_DTL_DCD = LOGIS_ADMIN.IAML_TB_BIDEALDIV_D.CUST_CLSF_DTL_CD INNER JOIN LOGIS_ADMIN.IAML_TB_DATDIV_M ON LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.DAT_DCD = LOGIS_ADMIN.IAML_TB_DATDIV_M.DAT_DCD where 1=1 and LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.coc_dt between '2024-01-01' and '2024-07-31' GROUP BY  LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.BIZNO     , LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.BIZCST_NM     , substr(LOGIS_ADMIN.IAMF_TB_COCBRANBYORGSALES_P.COC_DT,1,7) ) p group by bizno, bizcst_nm order by bizno;
